    max_iter = max_iter,
    cluster_method = cluster_method,
    k_clustering = k_clustering,
    ndim_pca_clustering = ndim_pca_clustering,
    k_knn = k_knn,
    ndim_pca_knn = ndim_pca_knn,
    sim_type = sim_type,
    distance_method_paths = distance_method_paths,
    ranks = ranks,
    num_cells = num_cells,
